(433, 'Поле 1', 388, '<!DOCTYPE html>\n<html lang=\"en-US\">\n<script src=\"https://ajax.googleapis.com/ajax/libs/angularjs/1.6.9/angular.min.js\"></script>\n<body>\n\n<div ng-app=\"\">\n  <p>Name : <input type=\"text\" ng-model=\"name\"></p>\n  <h1>Hello {{name}}</h1>\n</div>\n\n</body>\n</html>', '<!DOCTYPE html>\n<html lang=\"en-US\">\n<script src=\"https://ajax.googleapis.com/ajax/libs/angularjs/1.6.9/angular.min.js\"></script>\n<body>\n\n<div ng-app=\"\">\n  <p>Name : <input type=\"text\" ng-model=\"name\"></p>\n  <h1>Hello {{name}}</h1>\n</div>\n\n</body>\n</html>', 1),
