(698, 'Поле 1', 590, '<style>\n    html {\n        scroll-behavior: smooth;\n    }\n</style>\n<script>\n    (function($, window) {\n        var adjustAnchor = function() {\n\n            var $anchor = $(\':target\');\n            fixedElementHeight = 180;\n\n            if ($anchor.length > 0) {\n                $(\'html, body\')\n                    .stop()\n                    .animate({\n                        scrollTop: $anchor.offset().top - fixedElementHeight\n                    }, 10);\n\n\n            }\n        };\n\n        $(window).on(\'hashchange load\', function() {\n            adjustAnchor();\n        });\n\n    })(jQuery, window);\n</script>', '<style>\n    html {\n        scroll-behavior: smooth;\n    }\n</style>\n<script>\n    (function($, window) {\n        var adjustAnchor = function() {\n\n            var $anchor = $(\':target\');\n            fixedElementHeight = 180;\n\n            if ($anchor.length > 0) {\n                $(\'html, body\')\n                    .stop()\n                    .animate({\n                        scrollTop: $anchor.offset().top - fixedElementHeight\n                    }, 10);\n\n\n            }\n        };\n\n        $(window).on(\'hashchange load\', function() {\n            adjustAnchor();\n        });\n\n    })(jQuery, window);\n</script>', 1),
