(782, 'Поле 1', 672, 'view(\'welcome\', [\'data\' => $data]);\nview(\'welcome\', compact(\'data\'));\nview(\'welcome\')->with([\'data\' => $data]);\nview(\'welcome\')->withData($data);', 'view(\'welcome\', [\'data\' => $data]);\nview(\'welcome\', compact(\'data\'));\nview(\'welcome\')->with([\'data\' => $data]);\nview(\'welcome\')->withData($data);', 1),
(783, 'Поле 1', 673, 'ОТОБРАЖЕНИЕ ДАННЫХ В ШАБЛОНЕ', 'ОТОБРАЖЕНИЕ ДАННЫХ В ШАБЛОНЕ', 1),
(784, 'Поле 1', 674, '● Для отображения значения переменной в представлении используется {{ $var }}\n● Переменная обрабатывается функцией htmlentities()\n● Если нужно отобразить значение переменной без этой функции, нужно использовать {!! $var !!}\n● Если конструкция {{ }} на странице нужна для работы с js-фреймворками, можно использовать @{{ $var }}, чтобы Blade не обрабатывал скобки, а выводил как есть.', '● Для отображения значения переменной в представлении используется {{ $var }}\n● Переменная обрабатывается функцией htmlentities()\n● Если нужно отобразить значение переменной без этой функции, нужно использовать {!! $var !!}\n● Если конструкция {{ }} на странице нужна для работы с js-фреймворками, можно использовать @{{ $var }}, чтобы Blade не обрабатывал скобки, а выводил как есть.', 1),
(785, 'Поле 1', 675, 'УПРАВЛЯЮЩИЕ КОНСТРУКЦИИ', 'УПРАВЛЯЮЩИЕ КОНСТРУКЦИИ', 1),
(786, 'Поле 1', 676, 'Blade поддерживает следующие управляющие конструкции:\nif, if … else, isset, empty, for, foreach, while\n● указываются через @\n● используются с закрывающим тегом (end + название директивы)', 'Blade поддерживает следующие управляющие конструкции:\nif, if … else, isset, empty, for, foreach, while\n● указываются через @\n● используются с закрывающим тегом (end + название директивы)', 1),
