(949, 'Поле 1', 822, '<html>\n<head>\n<script src=\"gsap.min.js\"></script>\n<style>\nbody {\n  margin: 10px;\n}\n\n.box {\n  display: block;\n}\n</style>\n</head>\n<body>\n<h2 class=\"title\">gsap.to() Basic Usage</h2>\n<div class=\"box orange\" style=\"background: orange; width: 200px; height: 200px;\"></div>\n<div class=\"box grey\" style=\"background: grey; width: 200px; height: 200px;\"></div>\n<div class=\"box green\" style=\"background: green; width: 200px; height: 200px;\"></div>\n<script>\ngsap.to(\"h2.title\", {duration: 1, opacity: 0.3});\ngsap.to(\".box\", {duration: 12, x: 500});\ngsap.to(\".green\", {duration: 12, rotation: 360, scale: 0.5, backgroundColor: \"#560563\"});\ngsap.to(\".orange\", {duration: 12, rotation: 360, scale: 0.5, backgroundColor: \"#560563\"});\ngsap.to(\".grey\", {duration: 12, rotation: 1080, scale: 0.5, backgroundColor: \"#560563\"});\n</script>\n</body>\n</html>', '<html>\n<head>\n<script src=\"gsap.min.js\"></script>\n<style>\nbody {\n  margin: 10px;\n}\n\n.box {\n  display: block;\n}\n</style>\n</head>\n<body>\n<h2 class=\"title\">gsap.to() Basic Usage</h2>\n<div class=\"box orange\" style=\"background: orange; width: 200px; height: 200px;\"></div>\n<div class=\"box grey\" style=\"background: grey; width: 200px; height: 200px;\"></div>\n<div class=\"box green\" style=\"background: green; width: 200px; height: 200px;\"></div>\n<script>\ngsap.to(\"h2.title\", {duration: 1, opacity: 0.3});\ngsap.to(\".box\", {duration: 12, x: 500});\ngsap.to(\".green\", {duration: 12, rotation: 360, scale: 0.5, backgroundColor: \"#560563\"});\ngsap.to(\".orange\", {duration: 12, rotation: 360, scale: 0.5, backgroundColor: \"#560563\"});\ngsap.to(\".grey\", {duration: 12, rotation: 1080, scale: 0.5, backgroundColor: \"#560563\"});\n</script>\n</body>\n</html>', 1),
