(1065, 'Поле 1', 906, '<html>\n<head>\n<script src=\"gsap.min.js\"></script>\n<style>\nbody {\n  margin: 10px;\n}\n\n.box {\n  display: block;\n}\n</style>\n</head>\n<body>\n<h2 class=\"title\">gsap.to() Basic Usage</h2>\n<div class=\"box orange\" style=\"background: orange; width: 200px; height: 200px;\"></div>\n<div class=\"box grey\" id=\"grey\" style=\"background: grey; width: 200px; height: 200px;\"></div>\n<div class=\"box green\" style=\"background: green; width: 200px; height: 200px;\"></div>\n<script>\nvar mob=document.getElementById(\"grey\");\nvar i=0;\ngsap.to(\"h2.title\", {duration: 12, opacity: 0.3, ease: \"elastic\"});\ngsap.to(\".box\", {duration: 12});\ngsap.to(\".green\", {duration: 12, rotation: 360, scale: 0.5, backgroundColor: \"#560563\", borderRadius: \"20%\", border: \"5px solid red\", ease: \"back\", x: \"random(300,700)\", skewX: 360});\ngsap.to(\".orange\", {duration: 12, rotation: 360, scale: 0.5, backgroundColor: \"#560563\", borderRadius: \"20%\", border: \"5px solid red\", ease: \"elastic\", x: \"random(300,700)\", skewX: 360});\ngsap.to(mob, {duration: 12, rotation: 1080, scale: 0.5, ease: \"bounce\", x: \"random(300,700)\", onStart: tweenStart, onUpdate: tweenUpdate, onComplete: tweenComplete\n});\n\nfunction tweenComplete() {\n  console.log(\"the tween is complete\");\n}\nfunction tweenStart() {\n  console.log(\"tweenStart\");\n}\nfunction tweenUpdate() {\n  console.log(\"tweenUpdate\");\n}\n\n</script>\n</body>\n</html>', '<html>\n<head>\n<script src=\"gsap.min.js\"></script>\n<style>\nbody {\n  margin: 10px;\n}\n\n.box {\n  display: block;\n}\n</style>\n</head>\n<body>\n<h2 class=\"title\">gsap.to() Basic Usage</h2>\n<div class=\"box orange\" style=\"background: orange; width: 200px; height: 200px;\"></div>\n<div class=\"box grey\" id=\"grey\" style=\"background: grey; width: 200px; height: 200px;\"></div>\n<div class=\"box green\" style=\"background: green; width: 200px; height: 200px;\"></div>\n<script>\nvar mob=document.getElementById(\"grey\");\nvar i=0;\ngsap.to(\"h2.title\", {duration: 12, opacity: 0.3, ease: \"elastic\"});\ngsap.to(\".box\", {duration: 12});\ngsap.to(\".green\", {duration: 12, rotation: 360, scale: 0.5, backgroundColor: \"#560563\", borderRadius: \"20%\", border: \"5px solid red\", ease: \"back\", x: \"random(300,700)\", skewX: 360});\ngsap.to(\".orange\", {duration: 12, rotation: 360, scale: 0.5, backgroundColor: \"#560563\", borderRadius: \"20%\", border: \"5px solid red\", ease: \"elastic\", x: \"random(300,700)\", skewX: 360});\ngsap.to(mob, {duration: 12, rotation: 1080, scale: 0.5, ease: \"bounce\", x: \"random(300,700)\", onStart: tweenStart, onUpdate: tweenUpdate, onComplete: tweenComplete\n});\n\nfunction tweenComplete() {\n  console.log(\"the tween is complete\");\n}\nfunction tweenStart() {\n  console.log(\"tweenStart\");\n}\nfunction tweenUpdate() {\n  console.log(\"tweenUpdate\");\n}\n\n</script>\n</body>\n</html>', 1),
