ondition=0;\n                this.conditionNew=0;\n                axios.post(\'/home/ajaxRequestPostDeleteAnimals\', {\'id\':id})\n                    .then((response) => {\n                        this.animals=response.data;\n                    })\n                    .catch((error) => {\n                        console.log(error);\n                    });\n            },\n            postInsert: function (xTitle, xDescription, catSlug, vidSlug) {\n                this.xTitle=xTitle;\n                this.xDescription=xDescription;\n                this.selectedCategory=catSlug;\n                this.selectedView=vidSlug;\n                this.conditionEdit=0;\n                this.conditionNew=0;\n                this.condition=0;\n                axios.post(\'/home/ajaxRequestPostInsertAnimals\', {\'title\':xTitle, \'Description\': xDescription, \'catSlug\':catSlug, \'vidSlug\':vidSlug})\n                    .then((response) => {\n                        this.animals=response.data;\n                    })\n                    .catch((error) => {\n                        console.log(error);\n                    });\n            },\n            ShowNew: function (id) {\n                this.conditionEdit=0;\n                this.condition=0;\n                this.conditionNew=id;\n                this.xTitle=\'\';\n                this.xDescription=\'\';\n                this.selectedCategory=\'default\';\n                this.selectedView=\'default\';\n\n            },\n        }\n    });\n</script>\n</body>\n</html>', 1);
