(1457, 'Поле 1', 1183, '<template>\n    <div>\n        <div class=\"form-group\">\n            <router-link :to=\"{name: \'createCompany\'}\" class=\"btn btn-success\">Create new company</router-link>\n        </div>\n\n        <div class=\"panel panel-default\">\n            <div class=\"panel-heading\">Companies list</div>\n            <div class=\"panel-body\">\n                <table class=\"table table-bordered table-striped\">\n                    <thead>\n                    <tr>\n                        <th>Name</th>\n                        <th>Address</th>\n                        <th>Website</th>\n                        <th>Email</th>\n                        <th width=\"100\">&nbsp;</th>\n                    </tr>\n                    </thead>\n                    <tbody>\n                    <tr v-for=\"company, index in companies\">\n                        <td>{{ company.name }}</td>\n                        <td>{{ company.address }}</td>\n                        <td>{{ company.website }}</td>\n                        <td>{{ company.email }}</td>\n                        <td>\n                            <router-link :to=\"{name: \'editCompany\', params: {id: company.id}}\" class=\"btn btn-xs btn-default\">\n                                Edit\n                            </router-link>\n                            <a href=\"#\"\n                               class=\"btn btn-xs btn-danger\"\n                               v-on:click=\"deleteEntry(company.id, index)\">\n                                Delete\n                            </a>\n                        </td>\n                    </tr>\n                    </tbody>\n                </table>\n            </div>\n        </div>\n    </div>\n</template>\n\n<script>\n    export default {\n        data: function () {\n            return {\n                companies: []\n            }\n        },\n        mounted() {\n            var app = this;\n            axios.get(\'/home2\')\n                .then(function (resp) {\n                    app.companies = resp.data;\n                })\n                .catch(function (resp) {\n                    console.log(resp);\n                    alert(\"Could not load companies 7777\");\n                });\n        },\n        methods: {\n            deleteEntry(id, index) {\n                if (confirm(\"Do you really want to delete it?\")) {\n                    var app = this;\n                    axios.delete(\'/api/v1/companies/\' + id)\n                        .then(function (resp) {\n                            app.companies.splice(index, 1);\n                        })\n                        .catch(function (resp) {\n                            alert(\"Could not delete company\");\n                        });\n                }\n            }\n        }\n    }\n</script>', '<template>\n    <div>\n        <div class=\"form-group\">\n            <router-link :to=\"{name: \'createCompany\'}\" class=\"btn btn-success\">Create new company</router-link>\n        </div>\n\n        <div class=\"panel panel-default\">\n            <div class=\"panel-heading\">Companies list</div>\n            <div class=\"panel-body\">\n                <table class=\"table table-bordered table-striped\">\n                    <thead>\n                    <tr>\n                        <th>Name</th>\n                        <th>Address</th>\n                        <th>Website</th>\n                        <th>Email</th>\n                        <th width=\"100\">&nbsp;</th>\n                    </tr>\n                    </thead>\n                    <tbody>\n                    <tr v-for=\"company, index in companies\">\n                        <td>{{ company.name }}</td>\n                        <td>{{ company.address }}</td>\n                        <td>{{ company.website }}</td>\n                        <td>{{ company.email }}</td>\n                        <td>\n                            <router-link :to=\"{name: \'editCompany\', params: {id: company.id}}\" class=\"btn btn-xs btn-default\">\n                                Edit\n                            </router-link>\n                            <a href=\"#\"\n                               class=\"btn btn-xs btn-danger\"\n                               v-on:click=\"deleteEntry(company.id, index)\">\n                                Delete\n                            </a>\n                        </td>\n                    </tr>\n                    </tbody>\n                </table>\n            </div>\n        </div>\n    </div>\n</template>\n\n<script>\n    export default {\n        data: function () {\n            return {\n                companies: []\n            }\n        },\n        mounted() {\n            var app = this;\n            axios.get(\'/home2\')\n                .then(function (resp) {\n                    app.companies = resp.data;\n                })\n                .catch(function (resp) {\n                    console.log(resp);\n                    alert(\"Could not load companies 7777\");\n                });\n        },\n        methods: {\n            deleteEntry(id, index) {\n                if (confirm(\"Do you really want to delete it?\")) {\n                    var app = this;\n                    axios.delete(\'/api/v1/companies/\' + id)\n                        .then(function (resp) {\n                            app.companies.splice(index, 1);\n                        })\n                        .catch(function (resp) {\n                            alert(\"Could not delete company\");\n                        });\n                }\n            }\n        }\n    }\n</script>', 1),
